                    <div id='datePickers_home_DIV'>
                        <MaterialDatePicker />
                        <MaterialDatePicker />
                    </div>
                </div>
                <RoundedButton
                    button_CLASSNAME={'blue_ROUNDEDBUTTON extra_large_ROUNDEDBUTTON'}
                    button_ID={'services_header_BUTTON'}
                    span_text_CLASSNAME={'p-medium white_COLOR'}
                    button_TEXT={'Search'}
                    Arrow002={false}
                />
            </form>
        </>
    )
}

export default SearchForm
