import React, { useState } from 'react'

import axios from 'axios'

import { Redirect } from 'react-router-dom'

import './css/ValidButton.scss'

const ValidButton = (props) => {

    let response

    const [submitted, setSubmitted] = useState(false)

    const handleSubmit = (event) => {
        console.log('Form sent')

        event.preventDefault()
        axios.get('http://10.0.4.5:8000/vhe/VHE%20demo').then(result => {
            console.log(result)
            response = result.data
            setSubmitted(true)
            console.log(response)
        }).catch(e => {
            console.log('Error submitting form: ' + e)
        })
    }

    const pathname = props.pathname

    if (submitted) {
        return (
            <>
                <Redirect
                    push to={{
                        pathname: '/' + pathname,
                        state: {
                            /* location: location */
                        }
                    }}
                />
            </>
        )
    }

    return (
        <>
            <button
                type='button'
                id={props.button_ID}
                className={props.button_CLASSNAME}
                onClick={(event) => {
                    if(props.redirect_BOOLEAN) {
                        handleSubmit(event)
                    }
                }}
            >
                <span className={props.span_text_CLASSNAME}>
                    {props.button_TEXT}
                    {props.redirect_BOOLEAN}
                </span>
            </button>
        </>
    )
}

export default ValidButton
